
public class switches {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=2;
		switch(n) //parameter
		{
		case 1:
			System.out.println("Value is 1");
			break;
		case 2:
			System.out.println("Value is 2");
			break;
		case 3:
			System.out.println("Value is 3");
			break;
		default:
			System.out.println("none");	
			break;
		}
	}

}
